import React, {useEffect, useState} from "react";
import {useRef,useContext} from "react";
import AuthContext from './context/AuthProvider';
import axios from './api/axios';
const LOGIN_URL = '/auth';
export const Login = (props) => {
    const userRef=useRef();
    const errRef=useRef();
    const [email, setEmail] =useState('');
    const [password,setPass] =useState('');
    const [errMsg,setErrMsg] =useState('');
    const [success,setSuccess] =useState(false);
    const {setAuth} =useContext(AuthContext);


    useEffect(() =>{
        userRef.current.focus();
    },[])

    useEffect(() =>{
        setErrMsg('');
    },[email,password])

    

    const handleSubmit = async (e) => {
        e.preventDefault();

        try{
            const response = await axios.post(LOGIN_URL, 
                JSON.stringify({email,password}),
                {
                    headers:{'Content-Type':'application/json'},
                    withCredentials:true
                });
                console.log(JSON.stringify(response?.data));
                const accessToken=response?.data?.accessToken;
                const roles=response?.data?.roles;
                setAuth({email,password,roles,accessToken});
            setEmail('');
            setPass('');
            setSuccess(true);
        }catch(err){
            if(!err.response){
                setErrMsg('No Server Response');
            }
            else if(err.response?.status===400)
            {
                setErrMsg('Missing username or password');
            }
        }

        
    }

    return (
        <>
        {success?(
            <section>
                <h1>You are logged in</h1>
                <br></br>
                <p>
                    <a href="#">Go to home</a>
                </p>
            </section>
        ):(

        
        
        <div className="auth-form-container">
            <p ref={errRef} className={errMsg ? "errmsg" : "offScreen"} aria-live="assertive">
                {errMsg}
            </p>
            <h2>Login</h2>
            <form className="login-form" onSubmit={handleSubmit}>
                <label htmlFor="email">email</label>
                <input required ref={userRef} value={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="Youremail@gmail.com" id="email" name="email"/>
                
                <label htmlFor="password">password</label>
                <input required ref={userRef} value={password} onChange={(e) => setPass(e.target.value)} type="password" placeholder="*******" id="password" name="password"/>
                
                <button type="submit">Log In</button>
            </form>
            <button  className="link-btn" onClick={() =>props.onFormSwitch('register')}>Dont have an account? Register here.</button>
        </div>
        )}
        </>
    )
}
